namespace Septu\Models;

use Config;
use Septu\SeptuException;

/**
 * Septu permission Model
 */
class Permission extends \Eloquent
{
	/**
	 * Holds the permissions object
	 * @var object
	 */
	public static $_permission = null;


	/**
	 * holds the permissions table name
	 * @var string
	 */
	public static $table = 'auth_permissions';
	
	public static function make($identifier = '')
	{

		// if we have an id set, let's try to find the permission
		if ($identifier)
		{
			/**
			 * let's look the group up
			 */
			$permission = static::where('id', '=', $identifier)->first();

			if ($permission)
			{
				// if a user has been found, then return an instance with the user attached
				static::$_permission = $permission;
			}
			else
			{
				// could not locate a permission using the provided identifier, so let's throw an exception.
				throw new SeptuException(__('septu::septu.permission_does_not_exist'));
			}

			return $permission;
		}

		return new self();
	}

	/**
	 * Create a New permission
	 * 
	 * @param  string  $name  name of the permission we want to create
	 * 
	 * @return permission object
	 */
	public function create_permission($fields = array())
	{
		// activation hash
		if  ( ! array_key_exists('resource', $fields) or empty($fields['resource']))
		{
			throw new SeptuException(__('septu::septu.no_permission_resource_provided'));
		}

		// check to see if the permission already exists
		if ( $this->permission_exists($fields['resource']))
		{
			throw new SeptuException(__('septu::septu.permission_already_exists'));
		}

		return self::create($fields);
	}

	/**
	 * Create a New permission
	 * 
	 * @param  string  $name  name of the permission we want to create
	 * 
	 * @return Permission object
	 */
	public function update_permission($fields = array())
	{
		// make sure we have a user selected
		if ( ! static::$_permission)
		{
			throw new SeptuUserException(__('septu::septu.no_permission_selected'));
		}

		if (array_key_exists('resource', $fields))
		{
			if (static::$_permission != $fields['resource'] and ! $this->permission_exists($fields['resource']))
			{
				static::$_permission->resource = trim($fields['resource']);
			}

			// unset the email field since we no longer need it
			unset($fields['resource']);
		}

		if (array_key_exists('description', $fields))
		{
			static::$_permission->description = trim($fields['description']);

			// unset the email field since we no longer need it
			unset($fields['description']);
		}

		// update the user's
		return static::$_permission->save();
	}

	/**
	 * Checks to see if a group exists
	 * 
	 * @param  mixed  $identifier  group name or id
	 * 
	 * @return boolean
	 */
	public function permission_exists($resource)
	{
		// query the db
		$query = self::where('resource', '=', $resource);

		return $query->count() > 0;
	}
}
